mapper.insert:

	select seq_notice.nextval from dual


insert into notice(
	bo_no, bo_title, bo_content, bo_writer, bo_date
)values(
	#{boNo}, #{boTitle}, #{boContent},#{boWriter},sysdate
)

mapper.select:
  	select b.*
	  	from(
		  	select
		  		 a.*, row_number() over(order by a.bo_no desc) rnum
		  	 from(
		  		select 
			  		bo_no, bo_title, bo_content, bo_writer,
			  		to_char(bo_date,'yyyy-mm-dd hh24:mi:ss') bo_date, bo_hit
		  		from notice
		  		where 1=1

		  		order by bo_no desc
	  		)a
  		)b

  		where b.rnum >= #{startRow} and b.rnum <=#{endRow}
